import React from "react";
import dialogStyles from "../dialogs.module.css";

export const Massage = (props) => {
  return (
    <div className={dialogStyles.massage}>
      <img
        className={dialogStyles.userAvatar}
        src="data:image/jpeg;base64,"
        width="30px"
        height="30px"
      ></img>
      <div className={dialogStyles.userText}> {props.massage}</div>{" "}
    </div>
  );
};
